#!/bin/bash\n\n# Text Moderator AWS Deployment Script\n\nset -e\n\necho \"🚀 Deploying Text Moderator to AWS...\"\n\n# Check requirements\nif ! command -v sam &> /dev/null; then\n    echo \"❌ AWS SAM CLI not found. Please install it first.\"\n    echo \"   Visit: https://docs.aws.amazon.com/serverless-application-model/latest/developerguide/install-sam-cli.html\"\n    exit 1\nfi\n\nif ! command -v aws &> /dev/null; then\n    echo \"❌ AWS CLI not found. Please install it first.\"\n    exit 1\nfi\n\n# Check if AWS credentials are configured\nif ! aws sts get-caller-identity &> /dev/null; then\n    echo \"❌ AWS credentials not configured. Run 'aws configure' first.\"\n    exit 1\nfi\n\n# Get HuggingFace token\nif [ -z \"$HUGGINGFACE_TOKEN\" ]; then\n    echo \"🔑 Enter your HuggingFace token:\"\n    read -s HUGGINGFACE_TOKEN\nfi\n\n# Build and deploy\necho \"📦 Building SAM application...\"\nsam build -t aws/template.yaml\n\necho \"🚀 Deploying to AWS...\"\nsam deploy \\\n    --guided \\\n    --parameter-overrides HuggingFaceToken=\"$HUGGINGFACE_TOKEN\" \\\n    --capabilities CAPABILITY_IAM\n\necho \"✅ Deployment complete!\"\necho \"🌐 Your Text Moderator is now live on AWS!\"\n